DROP TABLE IF EXISTS Payment
;
CREATE TABLE Payment
(
	id INTEGER NOT NULL,
	bidder_id INTEGER NOT NULL,
	payment_type VARCHAR(50) NOT NULL,
	amount FLOAT(0) NOT NULL,
	payment_date DATE,
	notes TEXT,
	PRIMARY KEY (id),
	FOREIGN KEY (bidder_id) REFERENCES Bidder (id)	ON DELETE CASCADE
) 
;



